mapper.insert:

INSERT INTO board 
SET board_title = #{board_title}
	, board_contents = #{board_contents}
	, board_name = #{board_name}
	, board_date = NOW()

mapper.update:

UPDATE board
SET board_title = #{board_title}
	, board_contents = #{board_contents}
	, board_name = #{board_name}
WHERE board_idx = #{board_idx}


UPDATE board 
SET board_hit = board_hit + 1
WHERE board_idx = #{board_idx}
